import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

const SinglePlayList = ({setMusicInfo,data, setData}) => {
    const  param  = useParams()
    const [playlistId,setPlaylistId]=useState()
    const [playlist,setPlaylist]=useState();

    useEffect(()=>{
        setPlaylistId(param?.['*']);

        const jwtToken = document.cookie.split('=')[1];
        const option = {
            method: 'POST',
            body: JSON.stringify({
                jwtToken
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            }
        };

        fetch('https://music-pwa-api.iran.liara.run/api/users/auth', option)
            .then((res) => res.json())
            .then((data) => {
                if (jwtToken) {
                    if (data?.isLogin) {
                        setUserInfo(data?.user)
                        if (data?.user?.role == 'admin') {
                            setProfileRoute('/adminprofile')
                        } else if (data?.user?.role == 'user') {
                            setProfileRoute('/userprofile')
                        }
                    } else {
                        setProfileRoute("/login-signup")
                    }
                }
            })

    },[param])



    return (
        <div className="page-content-holder" style={{color:'white'}}>{playlistId}</div>
    )
}

export default SinglePlayList
