import React, { useState } from 'react'

const UploadMusic = () => {

    const [name, setName] = useState('');
    const [desc, setDesc] = useState('');
    const [artists, setArtists] = useState('');
    const [imageLink, setImageLink] = useState('');
    const [musicLink, setMusicLink] = useState('');

    const usernameChange = (e) => {
        setUsername(e.target.value);
    }

    const passwordChange = (e) => {
        setPassword(e.target.value);
    }

    const onClick = () => {

        const option = {
            method: 'POST',
            body: JSON.stringify({
                username,
                password
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            }
        };

        fetch('https://music-pwa-api.iran.liara.run/api/users/sign-in', option)
            .then((res) => res.json())
            .then((data) => {
                if (data.token) {
                    const d = new Date();
                    d.setTime(d.getTime() + (90 * 24 * 60 * 60 * 1000));
                    let expires = "expires=" + d.toUTCString();
                    document.cookie = `jwtToken=${data.token};${expires};path=/`;
                    if (data.userRole === 'admin') {
                        location.replace('/adminprofile');
                    } else if (data.userRole === 'user') {
                        location.replace('/userprofile');
                    }
                }

            })


    }


    return (
        <div className='form-holder'>
            <form >
                <input type="text" placeholder="نام آهنگ" onChange={(e) => usernameChange(e)} value={name} />
                <input type="text" placeholder="توضیحات آهنگ" onChange={(e) => passwordChange(e)} value={desc} />
                <input type="text" placeholder="خواننده ها" onChange={(e) => passwordChange(e)} value={artist} />
                <input type="text" placeholder="لینک تصویر آهنگ" onChange={(e) => passwordChange(e)} value={imageLink} />
                <input type="text" placeholder="لینک آهنگ" onChange={(e) => passwordChange(e)} value={musicLink} />
            </form>
            <button type="submit" className='form-btn' onClick={onClick}>ایجاد موزیک</button>
        </div>
    )
}

export default UploadMusic
