import React, { useState, useEffect } from 'react'
import useSound from 'use-sound'
import PlayArrowRoundedIcon from '@mui/icons-material/PlayArrowRounded';
import StopRoundedIcon from '@mui/icons-material/StopRounded';

const MusicPlayer = ({ musicInfo }) => {

  const [play, { stop,sound }] = useSound(musicInfo?.musicPath);
  const [isPlaying,setIsPlaying]=useState(false);

  useEffect(()=>{
    stop();
  },[musicInfo])

  const playingButton=()=>{


    if(isPlaying){
      setIsPlaying(false);
      // stop();

    } else {
      setIsPlaying(true);
      // play(20);
      document.getElementById('musicPlayer').play()
    }
  }

  return (
    <div className='musicPlayerHolder'>
      <div className='playingButton'>
        {!isPlaying ? <PlayArrowRoundedIcon onClick={playingButton} /> : <StopRoundedIcon onClick={playingButton} />}
      </div>
      <audio id="musicPlayer" src={musicInfo?.musicPath} autoPlay={true} controls={true}/>
    </div>
  )
}

export default MusicPlayer
