import React, { useState, useEffect } from 'react'
import useSound from 'use-sound'

const MusicPlayer = ({ musicInfo }) => {

  const [isPlaying, setIsPlaying] = useState(false);
  const [play, { pause, stop, duration, sound }] = useSound(null);

  const [currTime, setCurrTime] = useState({
    min: "",
    sec: "",
  });

  const [seconds, setSeconds] = useState();

  useEffect(() => {
    sound(musicInfo?.musicPath)
    console.log(sound)
    stop();
    const sec = duration / 1000;
    const min = Math.floor(sec / 60);
    const secRemain = Math.floor(sec % 60);
    const time = {
      min: min,
      sec: secRemain
    };
    console.log(time)
  }, [musicInfo])

  useEffect(() => {
    const interval = setInterval(() => {
      if (sound) {
        setSeconds(sound.seek([]));
        const min = Math.floor(sound.seek([]) / 60);
        const sec = Math.floor(sound.seek([]) % 60);
        setCurrTime({
          min,
          sec,
        });
      }
    }, 1000);
    return () => clearInterval(interval);
  }, [sound]);

  const playingButton = () => {
    if (isPlaying) {
      pause(); // this will pause the audio
      setIsPlaying(false);
    } else {
      play(); // this will play the audio
      setIsPlaying(true);
    }
  };
  return (
    <div>
      <p onClick={playingButton} style={{ color: 'white' }}>play---{seconds}--{currTime.min}:{currTime.sec}</p>
      {/* {<audio src={musicInfo?.musicPath} autoPlay={true}/>} */}
    </div>
  )
}

export default MusicPlayer
