import React, { useEffect,useState } from 'react'
import LoadingOval from '../componenets/LoadingOval/LoadingOval'
import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';
import { useParams } from 'react-router-dom'

const ArtistPage = ({data,setData}) => {
  const param = useParams()
  const [loading, setLoading] = useState(false)


  useEffect(() => {

    const option = {
      method: 'GET',
      body:JSON.stringify({
        playlistID:param?.['*']
      }),
      headers: {
        'Content-type': 'application/json; charset=UTF-8',
      }
    };
    setLoading(true);
    fetch(`https://music-pwa-api.iran.liara.run/api/musics/all?page=1&limit=${limit*9+9}`, option)
      .then((res) => res.json())
      .then((d) => {
        setData(d.musics)
        setLoading(false);
      })
      fetch('https://music-pwa-api.iran.liara.run/api/musics/count', option)
      .then((res) => res.json())
      .then((d) => {
        setMusicCount(d.count)
      })
  }, []);

  return (
    <div className='page-content-holder'>
        <Backdrop
        sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
        open={loading}
      >
        <CircularProgress color="inherit" />
      </Backdrop>
      <h2 className="music-section-title"></h2>
      <div className="music-section">
        {data?.map((q, index) => {
          return (
            <div key={index} className="post">
              <img src={q?.coverImagePath} alt="" onClick={()=>setMusicInfo(q)} className="song-image" />
              <h3 className="song-name">{q?.title}</h3>
              <h4 className="artist-name">{q?.artist?.map(r => { return (r + " ") })}</h4>
            </div>
          )
        })}
      </div>
    </div>
  )
}

export default ArtistPage
