import React from 'react'
import './PlayListListView.css'
import LoadingOval from '../componenets/LoadingOval/LoadingOval'
import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';

const PlayListListView = ({userInfo,setMusicInfo,data, setData}) => {

  const [loading, setLoading] = useState(false)

  useEffect(() => {
    const user = userInfo;
    const option = {
      method: 'GET',
      body:{
        user
      },
      headers: {
        'Content-type': 'application/json; charset=UTF-8',
      }
    };
    setLoading(true);
    fetch(`https://music-pwa-api.iran.liara.run/api/musics/all?page=1&limit=${limit*9+9}`, option)
      .then((res) => res.json())
      .then((d) => {
        setData(d.musics)
        setLoading(false);
      })
      fetch('https://music-pwa-api.iran.liara.run/api/musics/count', option)
      .then((res) => res.json())
      .then((d) => {
        setMusicCount(d.count)
      })
  }, []);

  if (!data) return (<LoadingOval />)
  return (
    <div>
      <Backdrop
        sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
        open={loading}
      >
        <CircularProgress color="inherit" />
      </Backdrop>
        <div className='single-playlist-item'>

        </div>
    </div>
  )
}

export default PlayListListView
